// Fetch recent posts
fetch('https://yourblog.com/ghost/api/content/posts/?key=API_KEY')
  .then(response => response.json())
  .then(data => {
    console.log(data.posts);
  });

// Get specific post
fetch('https://yourblog.com/ghost/api/content/posts/slug/hello-world/?key=API_KEY')
  .then(response => response.json())
  .then(data => {
    console.log(data.posts[0]);
  });

// Include authors and tags
fetch('https://yourblog.com/ghost/api/content/posts/?key=API_KEY&include=authors,tags')
  .then(response => response.json())
  .then(data => {
    data.posts.forEach(post => {
      console.log(post.title, post.authors, post.tags);
    });
  });
