-- Transaction with error handling
BEGIN;

UPDATE inventory SET quantity = quantity - 5 WHERE product_id = 101;

-- Check if we have enough inventory
IF (SELECT quantity FROM inventory WHERE product_id = 101) < 0 THEN
    ROLLBACK;
    RAISE NOTICE 'Insufficient inventory';
ELSE
    INSERT INTO orders (product_id, quantity) VALUES (101, 5);
    COMMIT;
END IF;

-- Savepoints for partial rollback
BEGIN;
UPDATE accounts SET balance = balance - 50 WHERE account_id = 1;
SAVEPOINT point1;
UPDATE accounts SET balance = balance + 50 WHERE account_id = 2;
-- If second update fails
ROLLBACK TO point1;
COMMIT;

